import React, { useState } from "react";
import { Link, NavLink, useNavigate } from "react-router-dom";
import Cookies from "js-cookie";
import "./index.css";
function SideBar() {
  const [colorChange1, setcolorChange1] = useState(true);
  const [colorChange2, setcolorChange2] = useState(false);
  const navigate = useNavigate();
  const role = Cookies.get("role");

  const logout = () => {
    console.log("logout working");
    Cookies.remove("role");
    Cookies.remove("id");
    Cookies.remove("jwtToken");

    navigate("/login", { replace: true });
  };
  const colorChangeDashboard = () => {
    setcolorChange1(true);
    setcolorChange2(false);
  };
  const colorChangeUsers = () => {
    setcolorChange2(true);
    setcolorChange1(false);
  };
  console.log(colorChange2);
  console.log(role, "check");
  return (
    <>
      <div className="sidebar-container">
        <div className="admin-container">
          <h1 className="heading-admin">
            {role === "user" ? "User" : "Admin"}
          </h1>
        </div>
        <hr className="sidebar-line" />
        {role === "admin" ? (
          <>
            <Link to="/"  className={({ isActive }) =>
                isActive ? "color-sidebar-para-list " : "sidebar-para-list"
              }>
              <p >Dashboard</p>
            </Link>
            <Link to="/executives" className={({ isActive }) =>
                isActive ? "color-sidebar-para-list " : "sidebar-para-list"
              }>
              <p className="sidebar-para-list"> Execuitives</p>
            </Link>
          </>
        ) : (
          <>
            <NavLink
              to="/"
              className={({ isActive }) =>
                isActive ? "color-sidebar-para-list " : "sidebar-para-list"
              }
            >
              <p
              // className={
              //   colorChange1
              //     ? "color-sidebar-para-list "
              //     : "sidebar-para-list"
              // }
              >
                Dashboard
              </p>
            </NavLink>
            <NavLink
              to="/users"
              className={({ isActive }) =>
                isActive ? "color-sidebar-para-list " : "sidebar-para-list"
              }
            >
              <p
              // className={
              //   colorChange2
              //     ? "color-sidebar-para-list "
              //     : "sidebar-para-list"
              // }
              >
                Users
              </p>
            </NavLink>
          </>
        )}

        <div className="logout-container">
          <button className="sidebar-button" onClick={logout}>
            Logout
          </button>
        </div>
      </div>
    </>
  );
}
export default SideBar;
